INSERT INTO creative (grade, activity_time, area, specialty, school_record_id) VALUES (1, 119, "자율활동", "1학기 봉사부장(2015.03.01.- 2015.08.16.)으로서 학교 봉사활동을 나갈 때 필요한 일들을 책임감 있게 진행하였고 교내외에서 주관하는 각종 봉사활동에 대해 소개함. 2학기 학급회장(2015.08.17.-2016.02.29.)으로서 반의 학습 분위기를 만들고 학급에서 필요한 허드렛일을 솔선수범하여 함으로서 친구들에게 많은 신뢰를 얻고 책임감있는 회장으로서 인정을 받음. 또한 친구들이 여러 학교 행사나 수행평가에서 소외되지 않도록 학급 분위기를 잘 형성함. 학급 수학멘토링 (2015.08.24-12.16.)에서 멘토로서 활동하여 멘티의 수학성적 향상은 위해 아낌없이 도와주는 모습을 보임. 2015학년도 대성고 축제 봉랑제(2015.08.27. - 08.28.)에서 부스명: 아니, 이 느낌은 DSBC의 운영자로 활동함. 축제 기간 전부터 부스에 필요한 물품 제작과 준비 등 부스 준비에 적극적으로 참여함. 부장과 의 호흡을 맞춰서 부스 운영에 문제가 생기지 않도록 도움. 부스의 원활한 운영을 위하여 부스 프로그램을 직접 운영하고, 적극적으로 부스 홍보를 다니는 등 자신이 맡은 업무를 충실하게 수행함. 부스 운영에 도움이 되는 다양한 아이디어를 적극적으로 제시함. 부스 홍보를 위한 판넬 제작을 함. 모든 행사가 끝난 후에도 남아서 부스 정리를 함. 2015학년도 학교축제 봉량제 기간 동안 대성아카데미에서 진행되는 강연회 중 [성공하는 기업가가되는 방법][복잡함 속의 단순함] 의 강연에 참여하였음. 경영학의 개념, 성공한 기업가들의 사례를 통해 기업가의 전략에 대해 알게 되었으며 성공한 기업가가 되기 위한 요소들에 대해 배움. 프렉탈을 통해 수학의 신비함을 느꼈으며 정수차원과 실수차원의 존재, 프렉탈이 자기 유사성 을 가진 도형으로 이 개념이 카오스 이론이나 날씨에도 적용된다는 사실에 큰 흥미를 가짐. 안중근의사 기념관 탐방(2015.09.16.)을 통해 안중 근 의사의 나라사랑의 마음과 굳은 의지, 용맹스러움에 감동했으며 자신 이라면 어떻게 했을까에 대해 생각해 봄. 또한 열사의 나라 사랑의 정신을 본받고 역사를 좀더 관심을 가지고 공부하고 나라를 더욱 사랑해야겠다고 다짐함. 반단합을 위한 합창에서 지휘자로 활동하였으며 음악의 템포 를 결정하고 사분의 사 박자를 몸에 익히기 위해 거울을 보면서 몸짓으로 박자를 계속 맞추면서 허리와 팔이 아플때까지 거듭 연습함. 자신의 역할 을 잘 하기 위해 부단히 노력하는 모습이 훌륭했으며 책임감과 끈기가 돋보였음. 이러한 모습이 반의 합창을 높은 수준이 되도록 하는데 크게 기여 함.", 1);
INSERT INTO creative (grade, activity_time, area, specialty, school_record_id) VALUES (3, 28, "동아리 활동", "사물인터넷은 사물과 사물이 연결되어있으므로 어느 한 개체가 사이버 공격을 받으면 연쇄적으로 번질 가능성이 크다. 최근 랜섬웨어와 같은 사건이 발생하면서 속수무책으로 당하는 경우가 많다. 따라서 정보보안이 매우 중요할 것이며, 개개인이 사물인터넷화가 이루어짐에 따라 개인정보 악용 가능성도 기하급수적으로 증가할 것이기 때문에 개인에 좀 더 초점을 맞춘 방어기제가 필요함을 깨닫는 계기가 되었음", 1);
INSERT INTO educational (grade, semester, subject, course, ranking, detail_and_specialty, school_record_id) VALUES (1, "1학기", "국어", "국어 1", 2, "수업 중 수업에 바른 자세로 경청하며 적극적으로 참여함. 자율적으로 공부 계획을 세워 성실히 공부한 결과 해다 과목에서 우수한 성취를 보임", 1);
INSERT INTO educational (grade, semester, subject, course, ranking, detail_and_specialty, school_record_id) VALUES (3, "1학기", "수학", "기하와 벡터", 5, "이차곡선의 정의를 유도할 수 있고 문제를 정의로 푸는 방법과 방정식으로 푸는 두 가지 방안을 적절히 이용할 수 있음. 3차원 공간 파트의 문제들이 한가지 풀이법에 한정되어 있지 않음을 깨닫고 정사영, 도형, 벡터, 좌표 등 다양한 풀이법을 만들어 보려는 노력을 보임", 1);
